package _21_40._32_LongestValidParentheses2

import org.junit.Test
import kotlin.test.assertEquals

/*
Given a string containing just the characters '(' and ')', find the length of the longest valid (well-formed) parentheses substring.
Example 1:
Input: "(()"
Output: 2
Explanation: The longest valid parentheses substring is "()"
Example 2:
Input: ")()())"
Output: 4
Explanation: The longest valid parentheses substring is "()()"
*/



class Solution {
    fun longestValidParentheses(s: String): Int {
        var maxlen = 0
        val dp:Array<Int> = Array(s.length) {0}
        for (i in 1 until s.length) {
            if (s[i] == ')') {
                if (s[i - 1] == '('){
                    dp[i] = if (i >= 2) dp[i-2] +2 else 2
                } else if ( i-dp[i-1] > 0 && s[i-dp[i-1]-1] == '('){
                    dp[i] = dp[i-1] + (if ((i-dp[i-1]) >=2) dp[i - dp[i-1] -2] + 2 else 2)
                }
                maxlen = Math.max(maxlen,dp[i])
            }
        }
        return maxlen
    }
}

class Tests{
    @Test fun Test1(){
        assertEquals(2, Solution().longestValidParentheses("(()"))
    }

    @Test fun Test2(){
        assertEquals(4, Solution().longestValidParentheses(")()())"))
    }

    @Test fun Test3(){
        assertEquals(2, Solution().longestValidParentheses("()(()"))
    }

